import React, { useState, useEffect } from "react";
import "./Hero.css";
import heroimg from "../assets/hero-section.png";

function Hero() {
  const [text, setText] = useState("");
  const [index, setIndex] = useState(0);
  const [deleting, setDeleting] = useState(false);
  const titleText = ["Data Analyst", "Excel Expert", "Graphic Designer"];

  useEffect(() => {
    let interval;
    if (!deleting && text.length < titleText[index].length) {
      interval = setInterval(() => {
        setText((prev) => prev + titleText[index][prev.length]);
      }, 120);
    } else if (text.length === titleText[index].length && !deleting) {
      setTimeout(() => setDeleting(true), 800);
    } else if (deleting && text.length > 0) {
      interval = setInterval(() => {
        setText((prev) => prev.slice(0, prev.length - 1));
      }, 60);
    } else if (deleting && text.length === 0) {
      setDeleting(false);
      setIndex((prev) => (prev + 1) % titleText.length);
    }
    return () => clearInterval(interval);
  }, [text, deleting, index]);

  return (
    <section className="hero py-5">
      <div className="container">
        <div className="row align-items-center gx-5">
          {/* Left Section */}
          <div className="col-lg-6 col-md-12 text-center text-lg-start mb-4 mb-lg-0">
            <h5 className="text-warning mb-3">Hello! This is Karthi</h5>
            <h1 className="fw-bold mb-3">
              I'm <span className="typing-text">{text}</span>
            </h1>
            <p className="lead mb-4">
              I'm a passionate Graphic Designer focused on creating visually stunning and functional designs.
            </p>

            <div className="d-flex flex-wrap gap-3 justify-content-center justify-content-lg-start">
              <a href="/resume.pdf" download className="custom-primary-btn">
                <i className="bi bi-download me-2"></i> Download Resume
              </a>
              <a href="/contact" className="custom-secondary-btn">
                <i className="bi bi-envelope-fill me-2"></i> Contact Me
              </a>
            </div>
          </div>

          {/* Right Image Section */}
          <div className="col-lg-6 col-md-12 text-center">
            <img src={heroimg} alt="Karthi" className="img-fluid floating-img" />
          </div>
        </div>
      </div>
    </section>
  );
}

export default Hero;
