import { useState } from "react";
import { ChevronDown, ChevronUp, CheckCircle } from "lucide-react";
import { Button } from "@/components/ui/button";

export default function ExperienceSection() {
  const [openSection, setOpenSection] = useState(null);

  const toggleSection = (index) => {
    setOpenSection(openSection === index ? null : index);
  };

  const sections = [
    {
      title: "Adarsh Solutions Pvt Ltd, Bangalore (August 2024 - Present)",
      content: (
        <div className="space-y-3">
          <p className="font-semibold">Position: Project Engineer</p>
          <p className="font-semibold">Current Roles and Responsibilities:</p>
          <ul className="list-none space-y-1 pl-2">
            {[
              "Prepare a valid Project Schedule using Primavera P6.",
              "Planning, Scheduling, and Management of the work package.",
              "Verify & update critical paths and interfaces between task activities.",
              "Prepare Key Performance Indicators (KPI), Cash Flow Charts, Histograms, Progress S-Curves, Manpower, and Quantity Reports.",
              "Weekly update on Material procurement.",
              "Status updates in daily status calls.",
              "Continuous improvement in project development.",
              "Prepare Effort Estimation and QAG purpose documentation."
            ].map((item, idx) => (
              <li key={idx} className="flex items-start gap-2">
                <CheckCircle className="w-4 h-4 text-yellow-500 mt-1" />
                <span>{item}</span>
              </li>
            ))}
          </ul>

          <p className="font-semibold">Skills:</p>
          <ul className="list-none space-y-1 pl-2">
            <li className="flex gap-2"><CheckCircle className="w-4 h-4 text-yellow-500 mt-1" />Functional: Documentation, team work, Excel VBA</li>
            <li className="flex gap-2"><CheckCircle className="w-4 h-4 text-yellow-500 mt-1" />Management: Training, Planning, Problem-solving</li>
            <li className="flex gap-2"><CheckCircle className="w-4 h-4 text-yellow-500 mt-1" />Tools: Primavera P6, MS Project, Excel VBA, SAP, AutoCAD</li>
          </ul>
        </div>
      )
    },
    {
      title: "Project Experience",
      content: (
        <div className="space-y-3">
          {[{
            name: "BH F&P&T Offshore Support",
            client: "Bakers Hughes, USA",
            tools: "Primavera P6 and MS Office",
            period: "March 2021 - August 2024"
          },
          {
            name: "Database Migration from Oracle to SAP",
            client: "Bakers Hughes, USA",
            tools: "Primavera P6 and MS Office",
            period: "March 2021 - August 2021"
          },
          {
            name: "Spare Part List Creation",
            client: "Bakers Hughes, USA",
            tools: "MS Project Plan 3 and MS Office",
            period: "March 2022 - Dec 2023"
          }].map((proj, idx) => (
            <div key={idx} className="border-l-4 border-yellow-500 pl-4">
              <p className="font-semibold">{proj.name}</p>
              <p>Client: {proj.client}</p>
              <p>Tools: {proj.tools}</p>
              <p>Period: {proj.period}</p>
            </div>
          ))}
        </div>
      )
    }
  ];

  return (
    <div className="space-y-4">
      {sections.map((section, idx) => (
        <div
          key={idx}
          className="rounded-xl border border-gray-300 overflow-hidden transition-all duration-300 hover:bg-white/70"
        >
          <button
            onClick={() => toggleSection(idx)}
            className="w-full flex justify-between items-center p-4 bg-gray-100 hover:bg-white/90 transition-colors duration-300 border-l-4 border-yellow-500"
          >
            <h2 className="text-lg font-semibold text-black">{section.title}</h2>
            {openSection === idx ? (
              <ChevronUp className="text-yellow-500" />
            ) : (
              <ChevronDown className="text-yellow-500" />
            )}
          </button>

          {openSection === idx && (
            <div className="p-4 text-black space-y-2 animate-fadeIn">
              {section.content}
              <Button variant="outline" className="text-yellow-600 border-yellow-600 hover:bg-yellow-50">View Details</Button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}
