import React, { useState, useEffect } from "react";
import "./Hero.css";
import heroimg from "../assets/hero-section.png"

function Hero() {
  const [text, setText] = useState("");
  const [index, setIndex] = useState(0);
  const [deleting, setDeleting] = useState(false);
  const titleText = ["Data Analyst", " & Excel expert"];

  useEffect(() => {
    let interval;

    if (!deleting && index < titleText.length) {
      // Typing Effect
      if (text.length < titleText[index].length) {
        interval = setInterval(() => {
          setText((prev) => prev + titleText[index][text.length]);
        }, 100);
      } else {
        setTimeout(() => setDeleting(true), 1000); // Delay before starting to delete
      }
    } else if (deleting) {
      // Deleting Effect
      if (text.length > 0) {
        interval = setInterval(() => {
          setText((prev) => prev.slice(0, prev.length - 1));
        }, 100);
      } else {
        setDeleting(false);
        setIndex((prev) => (prev + 1) % titleText.length); // Move to next title text
      }
    }

    return () => clearInterval(interval);
  }, [text, deleting, index]);

  return (
    <section className="hero">
      <div className="hero-content">
        <h6 className="welcome-text">Hlo! This is Karthi</h6> {/* Static Text */}
        <h4 className="typing-animation">I'm {text}</h4> {/* Typing Animation */}
      
        <p className="description">
          I'm a passionate Graphic Designer focused on creating visually stunning and functional designs.
        </p>
        <div className="hero-buttons">
          <a href="/resume.pdf" download className="btn primary-btn">
            Download Resume
          </a>
          <a href="/contact" className="btn secondary-btn">
            Contact Me
          </a>
        </div>
      </div>
      <div className="hero-image">
        <img src={heroimg} alt="Karthi" />
      </div>
    </section>
  );
}

export default Hero;
