import React, { useState, useEffect } from "react";
import "./Hero.css";

function Hero() {
  const [typing, setTyping] = useState("");
  const [index, setIndex] = useState(0);
  
  const titleText = ["Hlo! Welcome", "I'm Karthi"];
  const description = "I'm a passionate Graphic Designer focused on creating visually stunning and functional designs.";

  // Typing effect function
  useEffect(() => {
    const typingInterval = setInterval(() => {
      if (index < titleText[0].length) {
        setTyping((prev) => prev + titleText[0][index]);
        setIndex(index + 1);
      } else {
        clearInterval(typingInterval);
      }
    }, 100);

    return () => clearInterval(typingInterval); // Cleanup on unmount
  }, [index]);

  return (
    <section className="hero">
      <div className="hero-content">
        <h1>
          {typing}
          <span>{titleText[1]}</span>
        </h1>
        <p className="role">
          I'm a <span className="highlight">Graphic Designer</span>
        </p>
        <p className="description">{description}</p>
        <div className="hero-buttons">
          <a href="/resume.pdf" download className="btn primary-btn">Download Resume</a>
          <a href="/contact" className="btn secondary-btn">Contact Me</a>
        </div>
      </div>
      <div className="hero-image">
        <img src="/assets/hero-section.png" alt="Karthi" />
      </div>
    </section>
  );
}

export default Hero;
