import React, { useEffect, useRef } from "react";
import Isotope from "isotope-layout";
import imagesLoaded from "imagesloaded";
import GLightbox from "glightbox";
import portfolioimg1 from "../assets/img/portfolio/portfolio-1.webp"
import portfolioimg2 from "../assets/img/portfolio/portfolio-1.webp"
import portfolioimg3 from "../assets/img/portfolio/portfolio-1.webp"
import portfolioimg4 from "../assets/img/portfolio/portfolio-1.webp"

function Portfolio() {
    const isotopeContainer = useRef(null);
    const iso = useRef(null);
  
    useEffect(() => {
      // Init GLightbox
      GLightbox({
        selector: ".glightbox"
      });
  
      // Wait for images to load before initializing Isotope
      imagesLoaded(isotopeContainer.current, () => {
        iso.current = new Isotope(isotopeContainer.current, {
          itemSelector: ".isotope-item",
          layoutMode: "masonry",
          sortBy: "original-order"
        });
      });
  
      // Cleanup
      return () => {
        if (iso.current) iso.current.destroy();
      };
    }, []);
  
    // Filter handler
    const handleFilterClick = (e, filter) => {
      e.preventDefault();
      const active = document.querySelector(".filter-active");
      if (active) active.classList.remove("filter-active");
      e.target.classList.add("filter-active");
      iso.current.arrange({ filter });
    };
  return (
    <div> 
        <main className="main"> 
        <section id="portfolio" className="portfolio section">

{/* <!-- Section Title --> */}
<div className="container section-title" data-aos="fade-up">
  <h2>Portfolio</h2>
  <div className="title-shape">
    <svg viewBox="0 0 200 20" xmlns="http://www.w3.org/2000/svg">
      <path d="M 0,10 C 40,0 60,20 100,10 C 140,0 160,20 200,10" fill="none" stroke="currentColor" stroke-width="2"></path>
    </svg>
  </div>
  <p>Quis autem vel eum iure reprehenderit qui in ea voluptate velit esse quam nihil molestiae consequatur vel illum qui dolorem</p>
</div>
{/* <!-- End Section Title --> */}

<div className="container" data-aos="fade-up" data-aos-delay="100">

  <div className="isotope-layout" data-default-filter="*" data-layout="masonry" data-sort="original-order">

  <div className="portfolio-filters-container" data-aos="fade-up" data-aos-delay="200">
  <ul className="portfolio-filters isotope-filters">
  <li 
    data-filter="*" 
    className="filter-active" 
    onClick={(e) => handleFilterClick(e, "*")}
  >
    All Work
  </li>
  <li 
    data-filter=".filter-web" 
    onClick={(e) => handleFilterClick(e, ".filter-web")}
  >
    Power BI
  </li>
  <li 
    data-filter=".filter-graphics" 
    onClick={(e) => handleFilterClick(e, ".filter-graphics")}
  >
    Excel
  </li>
</ul>

    </div>

    <div className="row g-4 isotope-container" data-aos="fade-up" data-aos-delay="300" ref={isotopeContainer}>

      <div className="col-lg-6 col-md-6 portfolio-item isotope-item filter-web">
        <div className="portfolio-card">
          <div className="portfolio-image">
            <img src= {portfolioimg1} className="img-fluid" alt="" loading="lazy"/>
            <div className="portfolio-overlay">
              <div className="portfolio-actions">
                <a href= {portfolioimg1} className="glightbox preview-link" data-gallery="portfolio-gallery-web"><i className="bi bi-eye"></i></a>
                <a href="portfolio-details.html" className="details-link"><i className="bi bi-arrow-right"></i></a>
              </div>
            </div>
          </div>
          <div className="portfolio-content">
            <span className="category">Web Design</span>
            <h3>Modern Dashboard Interface</h3>
            <p>Maecenas faucibus mollis interdum sed posuere consectetur est at lobortis.</p>
          </div>
        </div>
      </div>
      {/* <!-- End Portfolio Item --> */}

      <div className="col-lg-6 col-md-6 portfolio-item isotope-item filter-graphics">
        <div className="portfolio-card">
          <div className="portfolio-image">
            <img src= {portfolioimg2} className="img-fluid" alt="" loading="lazy"/>
            <div className="portfolio-overlay">
              <div className="portfolio-actions">
                <a href={portfolioimg2} className="glightbox preview-link" data-gallery="portfolio-gallery-graphics"><i className="bi bi-eye"></i></a>
                <a href="portfolio-details.html" className="details-link"><i className="bi bi-arrow-right"></i></a>
              </div>
            </div>
