import React, { useState, useEffect } from "react";
import { FaMoon, FaSun, FaBars, FaTimes } from "react-icons/fa";
import { Link } from "react-router-dom";
import "./Navbar.css";

function Navbar() {
  const [darkMode, setDarkMode] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleDarkMode = () => setDarkMode(!darkMode);
  const toggleMenu = () => setMenuOpen(!menuOpen);

  useEffect(() => {
    document.body.className = darkMode ? "dark-mode" : "";
  }, [darkMode]);

  
  return (
    <div className="container">
    {/* <nav className={`navbar ${menuOpen ? "open" : ""}`}>
      <div className="logo">Sawera<span>Dev</span></div>

      <ul className={`nav-links ${menuOpen ? "show" : ""}`}>
        <li><Link to="/" onClick={() => setMenuOpen(false)}>Home</Link></li>
        <li><Link to="/about" onClick={() => setMenuOpen(false)}>About</Link></li>
        <li><Link to="/projects" onClick={() => setMenuOpen(false)}>Projects</Link></li>
        <li><Link to="/contact" onClick={() => setMenuOpen(false)}>Contact</Link></li>
      </ul>

      <div className="nav-icons">
        <button onClick={toggleDarkMode} className="mode-toggle">
          {darkMode ? <FaSun /> : <FaMoon />}
        </button>

        <button onClick={toggleMenu} className="menu-toggle">
          {menuOpen ? <FaTimes /> : <FaBars />}
        </button>
      </div>
    </nav> */}

    <header id="header" className="header d-flex align-items-center sticky-top">
    <div className="header-container container-fluid container-xl position-relative d-flex align-items-center justify-content-between">

      <a href="index.html" className="logo d-flex align-items-center me-auto me-xl-0">
     <img src="assets/img/logo.webp" alt=""/>
        <h1 className="sitename">PortFolio</h1>
      </a>

      <nav id="navmenu" className={`navmenu ${menuOpen ? "mobile-nav-active" : ""}`}>
  <ul>
    <li><a href="#hero" onClick={() => setMenuOpen(false)} className="active">Home</a></li>
    <li><a href="#about" onClick={() => setMenuOpen(false)}>About</a></li>
    <li><a href="#resume" onClick={() => setMenuOpen(false)}>Resume</a></li>
    <li><a href="#portfolio" onClick={() => setMenuOpen(false)}>Portfolio</a></li>
    <li><a href="#services" onClick={() => setMenuOpen(false)}>Services</a></li>
    <li><a href="#contact" onClick={() => setMenuOpen(false)}>Contact</a></li>
  </ul>
  <button onClick={toggleMenu} className="mobile-nav-toggle d-xl-none">
    {menuOpen ? <FaTimes /> : <FaBars />}
  </button>
</nav>

{/* 
      <div className="header-social-links">
      <button onClick={toggleDarkMode} className="mode-toggle">
          {darkMode ? <FaSun /> : <FaMoon />}
        </button>
      </div> */}

    </div>
  </header>

    </div>
  );
}

export default Navbar;
